/**
 * @module types
 */

import { Optional } from './union';

/**
 * An object with arbitrary string-indexed values of an optional generic type `Optional<T>`.
 * `T` defaults to `any` when not explicitly supplied.
 */
export interface Dictionary<T = any> { [key: string]: Optional<T>; } // tslint:disable-line:no-any

 /**
  * Any object with both a numeric index signature with values of type `T` and a numeric `length`
  * property.  `T` defaults to `any` if unspecified.
  */
export interface AnyArrayLike<T = any> extends ArrayLike<T> { } // tslint:disable-line:no-any
